# We redefine take() and put() so that we can change
# the robot's appearance based on the type of
# protection carried

reeborg = default_robot()
reeborg.set_model("classic") # override user's choice
old_take = take
def take(obj=None):
    if carries_object():
        raise ReeborgError("I can only carry a single object.")
    if obj is None:
        old_take()
    else:
        old_take(obj)
    if carries_object("water protection"):
        reeborg.set_model("light blue")
    elif carries_object("fire protection"):
        reeborg.set_model("yellow")

old_put = put
def put(obj=None):
    if obj is None:
        old_put()
    else:
        old_put(obj)
    reeborg.set_model("classic")
